        db.execSQL("INSERT INTO bilgiler_tablo (ozID,sehirId,baslik,aciklama) VALUES (8,1,'','Adana Köprü Başı\nAdana Yollarında\nAğlarım (Yirmi Sene Gurbet Kahrı)')");
        db.execSQL("INSERT INTO bilgiler_tablo (ozID,sehirId,baslik,aciklama) VALUES (8,2,'','Telli Turna\nİki Kız Gider\nKara Dağın Boz Yılanı')");
        db.execSQL("INSERT INTO bilgiler_tablo (ozID,sehirId,baslik,aciklama) VALUES (8,3,'','Ben Giderim Oduna\nCevizin Yaprağı Dal Arasında\nDam Başına Asa Goymuş Galbırı')");



