    @Override
    public void onUpgrade(SQLiteDatabase db, int i, int i1) {
        db.execSQL("DROP TABLE IF EXISTS sehirler_tablo");
        db.execSQL("DROP TABLE IF EXISTS ozellik_tablo");
        db.execSQL("DROP TABLE IF EXISTS bilgiler_tablo");
        onCreate(db);

    }






